package nan;

public class reversestringwithoutusing3rdvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s="india";

for(int i=0;i<s.length();i++)
{
	System.out.println(s.charAt(i));
	
}
for(int i=s.length()-1; i>=0;i--)
{
	System.out.println(s.charAt(i));
	
}
	}

}
